import React, { Component } from 'react';

class Tabs extends Component {
	onTabChange = (selectedType) => {
		this.props.onTabSelected(selectedType);
	};

	render() {
		return (
			<div>
				<div>
					<span
						className={`tab ${this.props.selectedType === 'now-showing' ? 'selected' : ''}`}
						onClick={this.onTabChange('now-showing')}>
						Now Showing
					</span>
					<span
						className={`tab ${this.props.selectedType === 'top.rated' ? 'selected' : ''}`}
						onClick={this.onTabChange('top.rated')}>
						Top Rated
					</span>
				</div>
			</div>
		);
	}
}

export default Tabs;
