import React, { Component } from 'react';

class Tabs extends Component {
	constructor(props) {
		super(props);
		this.state = {
			selectedType : this.props.selectedType
		};
	}

	onTabChange = (event) => {
		const selectedType = event.target.getAttribute('data-type');
		this.setState({ selectedType });
		if (this.props.onTabSelected) {
			this.props.onTabSelected(selectedType);
		}
	};

	render() {
		const { selectedType } = this.state,
			isNowShowingSelected = selectedType === 'now_showing',
			isTopRatedSelected = selectedType === 'top_rated';
		return (
			<div>
				<div>
					<span className={`tab ${isNowShowingSelected ? 'selected' : ''}`} onClick={this.onTabChange}>
						Now Showing
					</span>
					<span className={`tab ${isTopRatedSelected ? 'selected' : ''}`} onClick={this.onTabChange}>
						Top Rated
					</span>
				</div>
			</div>
		);
	}
}

export default Tabs;
