import React, { Component } from 'react';

class Tabs extends Component {
	constructor(props) {
		super(props);
		this.state = {
			selectedType : this.props.selectedType
		};
	}

	onTabChange = (selectedType) => {
		this.setState({ selectedType });

		if (this.props.onTabSelected) {
			this.props.onTabSelected(selectedType);
		}
	};

	render() {
		return (
			<div>
				<div>
					<span
						className={`tab ${this.state.selectedType === 'now-showing' ? 'selected' : ''}`}
						onClick={this.onTabChange('now-showing')}>
						Now Showing
					</span>
					<span
						className={`tab ${this.state.selectedType === 'top.rated' ? 'selected' : ''}`}
						onClick={this.onTabChange('top.rated')}>
						Top Rated
					</span>
				</div>
			</div>
		);
	}
}

export default Tabs;
